let docWidth = document.documentElement.clientWidth;
let docScale = 1
setScale()
function setScale(){
	let designWidth = 1920;
	let designHeight = 1200;
/* 	let designWidth = 1512;
	let designHeight = 982; */

	docScale = document.documentElement.clientWidth/document.documentElement.clientHeight < designWidth/designHeight ? 
		  (document.documentElement.clientWidth / designWidth):
		  (document.documentElement.clientHeight / designHeight);
	document.querySelector('#screen').style.transform = `scale(${docScale}) translate(-50%)`;
}

window.onresize = function () {
	setScale()
};
